import { Component, OnInit, ChangeDetectorRef, ChangeDetectionStrategy, OnDestroy } from '@angular/core';
import {MatDialog,} from '@angular/material/dialog';
import { OfficeDialogComponent } from './office-dialog/office-dialog.component';
import { AddOfficeComponent } from './add-office/add-office.component';
import { BehaviorSubject, Observable, of, Subscription } from 'rxjs';
import { Office } from 'src/models/staff.models';
import { OfficeService } from '../shared/office.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class HomeComponent implements OnInit ,OnDestroy {

  private readonly subscriptions: Subscription = new Subscription();

  public officeList: Array<Office> = [];

  public offices$: BehaviorSubject<Office[]> = new BehaviorSubject<Office[]>(this.officeList) ;

  constructor(public dialog:MatDialog,
  public  officeservice:OfficeService,

  private readonly changeDetectionRef: ChangeDetectorRef,
  ) { }


  public ngOnInit(): void {
      this.officeservice.getOffices().subscribe(data =>
        data.map(acttions => {
          const value = acttions.payload.doc.data() as Office
          value.id =acttions.payload.doc.id;

          this.officeList.push(value)
                 this.offices$?.next(this.officeList)
                 this.changeDetectionRef.detectChanges();

         }

         ))

  }


  openAlertDialog(id:any) {
   this.dialog.open(OfficeDialogComponent,{
      width: '250px',
      height:'100px',
      data:{key:id   },
    });

  }

  public addOffice(){
    this.dialog.open(AddOfficeComponent,{

    })

}
public ngOnDestroy(): void {
this.subscriptions.unsubscribe()
this.offices$.subscribe()
}

}
